import React, { ReactElement } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Navigation as PolarisNavigation } from '@shopify/polaris';
import { HomeMajor, QuestionMarkMajor, ChatMajor, SettingsMajor } from '@shopify/polaris-icons';
import Logo from './Logo';

const Navigation = (): ReactElement => {
  const location = useLocation();
  const navigate = useNavigate();

  // Check which path is currently active
  const isActive = (path: string) => location.pathname === path;

  return (
    <PolarisNavigation
      location="/"
    >
      <div style={{ 
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '16px 0',
        background: '#141B2F',
        borderBottom: '1px solid rgba(227, 166, 32, 0.5)'
      }}>
        <svg 
          width={235} 
          height={40} 
          viewBox="0 0 409.96 69.94" 
          aria-label="Web3 Diagnostics logo"
        >
          <defs>
            <style>
              {`
                .cls-1 {
                  fill: #e3a620;
                  stroke-width: 0px;
                }
                .cls-2 {
                  fill: #fff;
                  stroke-width: 0px;
                }
              `}
            </style>
          </defs>
          <g>
            <g>
              <path className="cls-1" d="M101.81,69.69h-.15c-7.78-.76-11.09-4.37-12.49-7.27-2.74-5.65-.29-12.75,1.24-15.12l14.84-23.19V6.22h-5.06V.25h27.58v5.97h-5.33v13.89l9.87-.06-5.83,13.8c2.37.59,4.44,1.68,6.14,3.23,2.49,2.29,3.94,5.47,3.97,8.74.03,3.57-1.54,6.86-4.42,9.28-3.04,2.55-7.39,3.95-12.58,4.04-.21,0-11.03,0-11.03,0l2.66-5.97s8.04,0,8.28,0c3.79-.07,6.84-.98,8.82-2.64,1.49-1.25,2.3-2.9,2.29-4.66-.03-3.26-2.93-6.56-8.42-6.56h-4.5l5.6-13.24-6.82.04V6.22h-5.25v19.64l-15.79,24.67c-.65,1.01-2.53,5.89-.89,9.29,1.06,2.19,3.6,3.5,7.56,3.9h18.67c7.55-.27,13.05-2.68,16.8-7.38l2.13-3.2,5.69,4.34-1.87,2.34c-5.31,6.64-11.12,9.59-19.44,9.87h-.06s-22.23,0-22.23,0Z"/>
