            <g>
              <path className="cls-2" d="M0,11.91h7.73l1.63,9.12,1.58,9.12,4.22-18.24h7.39l2.16,9.12,2.16,9.07,1.54-9.07,1.63-9.12h7.44l-5.86,26.59h-9.07l-1.92-8.5-1.92-8.59-1.87,8.59-1.92,8.5H5.86L0,11.91Z"/>
              <path className="cls-2" d="M37.44,25.21c0-8.64,5.66-14.02,13.87-14.02,7.73,0,13.54,5.14,13.54,14.35v.58h-19.11c.24,4.95,2.3,7.35,5.62,7.35,2.64,0,4.46-1.58,5.18-5.04h8.06c-1.25,6.77-6.14,10.8-13.34,10.8-8.26,0-13.83-5.33-13.83-14.02ZM46.03,21.85h10.42c-.82-3.36-2.64-5.04-5.18-5.04-2.69,0-4.51,1.68-5.23,5.04Z"/>
              <path className="cls-2" d="M66.96,4.9h8.54v10.75c1.87-2.78,4.75-4.32,8.26-4.32,7.06,0,11.57,5.28,11.57,13.87s-4.61,13.87-11.57,13.87c-3.5,0-6.38-1.54-8.26-4.32v3.74h-8.54V4.9ZM86.88,25.21c0-5.38-2.11-8.06-5.66-8.06s-5.71,2.69-5.71,8.06,2.16,8.11,5.71,8.11,5.66-2.69,5.66-8.11Z"/>
            </g>
          </g>
        </svg>
      </div>
      <PolarisNavigation.Section
        items={[
          {
            url: '/',
            label: 'Home',
            icon: HomeMajor,
            selected: isActive('/'),
            onClick: () => {navigate('/'); return false;}
          },
          {
            url: '/instructions',
            label: 'Instructions',
            icon: QuestionMarkMajor,
            selected: isActive('/instructions'),
            onClick: () => {navigate('/instructions'); return false;}
          },
          {
            url: '/support',
            label: 'Support',
            icon: ChatMajor,
            selected: isActive('/support'),
            onClick: () => {navigate('/support'); return false;}
          },
          {
            url: '/settings',
            label: 'Settings',
            icon: SettingsMajor,
            selected: isActive('/settings'),
            onClick: () => {navigate('/settings'); return false;}
          }
        ]}
      />
    </PolarisNavigation>
  );
};

export default Navigation;
