import { tokensToRems, createExact } from '../../utilities.mjs';
import { border } from './border.mjs';
import { breakpoints } from './breakpoints.mjs';
import { color } from './color.mjs';
import { font } from './font.mjs';
import { height } from './height.mjs';
import { motion } from './motion.mjs';
import { shadow } from './shadow.mjs';
import { space } from './space.mjs';
import { text } from './text.mjs';
import { width } from './width.mjs';
import { zIndex } from './zIndex.mjs';

var createMetaThemeBase = createExact();
var metaThemeBase = createMetaThemeBase({
  border: tokensToRems(border),
  breakpoints: tokensToRems(breakpoints),
  color: color,
  font: tokensToRems(font),
  height: tokensToRems(height),
  motion: motion,
  shadow: tokensToRems(shadow),
  space: tokensToRems(space),
  text: text,
  width: tokensToRems(width),
  zIndex: zIndex
});

export { metaThemeBase };
