import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Frame, AppProvider } from '@shopify/polaris';
import Navigation from './components/Navigation';
import Logo from './components/Logo';
import Dashboard from './pages/Dashboard';
import Instructions from './pages/Instructions';
import Support from './pages/Support';
import Settings from './pages/Settings';
import '@shopify/polaris/build/esm/styles.css';

// Define the App component with proper typing
const App: React.FC = () => {
  // Define custom theme with Web3 Diagnostics gold color (#e3a620)
  const theme = {
    colors: {
      surface: '#FFFFFF',
      onSurface: '#212B36',
      interactive: '#e3a620',
      secondary: '#e3a620',
      primary: '#e3a620',
      critical: '#D82C0D',
      warning: '#FFC453',
      highlight: '#5BCDDA',
      success: '#008060',
      decorative: '#e3a620'
    },
    logo: {
      width: 124,
      topBarSource: '',
      contextualSaveBarSource: '',
      accessibilityLabel: 'Web3 Diagnostics',
    }
  };

  return (
    <AppProvider theme={theme} i18n={{}}>
      <Router>
        <Frame
          logo={<Logo height={40} width={180} />}
          navigation={<Navigation />}
        >
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/instructions" element={<Instructions />} />
            <Route path="/support" element={<Support />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </Frame>
      </Router>
    </AppProvider>
  );
};

export default App;
